mapper.select:
select e.employeeid, m.meetingid,m.meetingname,m.signinstarttime,m.signinendtime, m.starttime, m.endtime, m.roomid, m.reservationistid,m.numberofparticipants,m.description,m.reservationtime, e.employeename as reservationistname, mr.roomname as roomname from meeting as m,employee as e,meetingroom as mr where m.reservationistid = e.employeeid and m.roomid = mr.roomid and m.status = 0
and meetingname like concat('%',#{mdto.meetingname},'%')
and mr.roomname like concat('%',#{mdto.roomname},'%')
and e.employeename like concat('%',#{mdto.reservationistname},'%')
and starttime >= #{mdto.starttime}
and endtime <= #{mdto.endtime}
and m.reservationtime >= #{mdto.reservationtime}

select meeting.*
from meeting,
     meetingparticipants
where meetingparticipants.meetingid = meeting.meetingid
  and meetingparticipants.employeeid = #{employeeid}
  and status = 0
